    input.blur(function(){
        //将输入框的文本保存
        var input = $(this);
        var inputText = input.val();
        //将td的内容，即输入框去掉,然后给td赋值
        var td = input.parent("td");
        var productId = input.parent().parent().find("input").val();
        console.log(inputText);
        console.log(productId);
        var callback = function(msg){
            if (commCallback(msg)) {
                td.html(inputText);
//                            alert("修改商品成功");
//                            window.location.reload(true);
            }
        };
        commonPost('/admin/mallStandardProcurement/updateCommodityStock',
                {"recId":$("#recId").val(),"productId":productId,"stock":inputText}, callback, 'json');
        //让td重新拥有点击事件
        td.click(tdClick);
    });
    input.keyup(function(event){
        //1.获取当前用户按下的键值
        //解决不同浏览器获得事件对象的差异,
        // IE用自动提供window.event，而其他浏览器必须显示的提供，即在方法参数中加上event
        var myEvent = event || window.event;
        var keyCode = myEvent.keyCode;
        //2.判断是否是ESC键按下
        if(keyCode == 27){
            //将input输入框的值还原成修改之前的值
            input.val(tdText);
        }
    });
    //将输入框中的文本高亮选中
    //将jquery对象转化为DOM对象
    var inputDom = input.get(0);
    inputDom.select();
    //将td的点击事件移除
    td.unbind("click");
}
```

